package net.prachit.dsa.Strings;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

class LongestNonRepeatingSubStringTest {

    @Test
    void lengthOfLongestSubstring() {

        Assertions.assertEquals(3,new LongestNonRepeatingSubString().bruteForce("abcabca"));
        Assertions.assertEquals(1,new LongestNonRepeatingSubString().bruteForce(" "));

    }

    @Test
    void lengthOfStringSlidingWindow() {

        Assertions.assertEquals(3,new LongestNonRepeatingSubString().slidingWindow("abcabca"));
        Assertions.assertEquals(1,new LongestNonRepeatingSubString().slidingWindow(" "));
        Assertions.assertEquals(19 , new LongestNonRepeatingSubString().slidingWindow(""));
    }
}